sources.scss/main.scss:
@use 'sass:meta';

@layer abstracts {
    @include meta.load-css("abstracts");
}

@layer base {
    @include meta.load-css("base");
}

@layer layouts {
    @include meta.load-css("layouts");
}

@layer modules {
    @include meta.load-css("modules");
}

sources.scss/abstracts/_config.scss:
:root {

    /* color */
    --clr-primary-600: 300, 43%, 22%;
    --clr-secondary-300: 333, 80%, 67%;

    --clr-neutral-100: 0, 0%, 100%;
    --clr-neutral-300: 300, 24%, 96%;
    --clr-neutral-600: 303, 10%, 53%;


    /* font */
    --ff-primary: 'League Spartan', sans-serif;

    --fs-1: 1.0625rem; // 17px
    --fs-2: 1.1875rem; // 19px
    --fs-5: 2.5rem;    // 40px
    --fs-6: 3.5rem;    // 56px

    --fw-regular: 400;
    --fw-medium: 500;
    --fw-bold: 700;

    /* border radius */
    --border-radius-sm: 0.5rem;

}

/* responsive variables */
$break1: "(max-width:767.99px)";
$break2: "(min-width:768px) and (max-width:1198.99px)";
$break2open: "(min-width:768px)";
$break3open: "(min-width:1199px)";

