/***
    The new CSS reset - version 1.8.4 (last updated 14.2.2023)
    GitHub page: https://github.com/elad2412/the-new-css-reset
***/

/*
    Remove all the styles of the "User-Agent-Stylesheet", except for the 'display' property
    - The "symbol *" part is to solve Firefox SVG sprite bug
 */
 *:where(:not(html, iframe, canvas, img, svg, video, audio):not(svg *, symbol *)) {
    all: unset;
    display: revert;
}

/* Preferred box-sizing value */
*,
*::before,
*::after {
    box-sizing: border-box;
}

/* Reapply the pointer cursor for anchor tags */
a, button {
    cursor: revert;
}

/* Remove list styles (bullets/numbers) */
ol, ul, menu {
    list-style: none;
}

/* For images to not be able to exceed their container */
img {
    max-inline-size: 100%;
    max-block-size: 100%;
}

/* removes spacing between cells in tables */
table {
    border-collapse: collapse;
}

/* Safari - solving issue when using user-select:none on the <body> text input doesn't working */
input, textarea {
    -webkit-user-select: auto;
    user-select: auto;
}

/* revert the 'white-space' property for textarea elements on Safari */
textarea {
    white-space: revert;
}

/* minimum style to allow to style meter element */
meter {
    -webkit-appearance: revert;
    appearance: revert;
}

/* preformatted text - use only for this feature */
:where(pre) {
    all: revert;
}

/* reset default text opacity of input placeholder */
::placeholder {
    color: unset;
}

/* remove default dot (â€¢) sign */
::marker {
    content: initial;
}

/* fix the feature of 'hidden' attribute.
   display:revert; revert to element instead of attribute */
:where([hidden]) {
    display: none;
}

/* revert for bug in Chromium browsers
   - fix for the content editable attribute will work properly.
   - webkit-user-select: auto; added for Safari in case of using user-select:none on wrapper element*/
:where([contenteditable]:not([contenteditable="false"])) {
    -moz-user-modify: read-write;
    -webkit-user-modify: read-write;
    overflow-wrap: break-word;
    -webkit-line-break: after-white-space;
    line-break: after-white-space;
    -webkit-user-select: auto;
    user-select: auto;
}

/* apply back the draggable feature - exist only in Chromium and Safari */
:where([draggable="true"]) {
    -webkit-user-drag: element;
}

/* Revert Modal native behavior */
:where(dialog:modal) {
    all: revert;
}

sources.scss/abstracts/_variables.scss:
:root {

    /* colors */
    --clr-primary-400: 273, 85%, 13%;
    --clr-secondary-400: 150, 100%, 63%;

    --clr-neutral-100: 0, 0%, 100%;
    --clr-neutral-300: 276, 100%, 99%;
    --clr-neutral-600: 271, 12%, 34%;

    /* typography */
    --ff-primary: 'Manrope', sans-serif;
    --ff-secondary: 'Fraunces', sans-serif;
    --ff-body: var(--ff-primary);
    --ff-heading: var(--ff-secondary);

    --fs-1: 1rem;     // 16px
    --fs-2: 1.125rem; // 18px
    --fs-3: 1.75rem;  // 28px
    --fs-4: 2rem;     // 32px
    --fs-5: 3.125rem; // 50px
    --fs-6: 3.5rem;   // 56px
    --fs-7: 3.75rem;  // 60px
    --fs-8: 5rem;;    // 80px

    --fs-body-1: var(--fs-1);
    --fs-body-2: var(--fs-2);

    --fw-regular: 400;
    --fw-semi-bold: 600;
    --fw-bold: 700;
}

/* breakpoints */
$break1: "(max-width:767.99px)";
$break2: "(min-width:768px) and (max-width:1198.99px)";
$break2open: "(min-width:768px)";
$break3open: "(min-width:1199px)";

sources.scss/base/_typography.scss:
@use '../abstracts/variables' as *;

body {
    font-family: var(--ff-body);

    @media #{$break1}, #{$break2} {
        font-size: var(--fs-body-1);
        line-height: 1.75;
        color: hsl(var(--clr-neutral-600));
    }

    @media #{$break3open} {
        font-size: var(--fs-body-2);
        line-height: 1.777;
        color: hsl(var(--clr-primary-400));
    }
}

h1, h2, h3, h4, h5, h6 {
    font-family: var(--ff-heading);
}

.heading-1 {
    line-height: 1;
    @media #{$break1} {
        font-size: var(--fs-5);
    }
    @media #{$break2} {
        font-size: var(--fs-6);
    }
    @media #{$break3open} {
        font-size: var(--fs-8);
    }
}

.heading-2 {
    font-size: var(--fs-6);
    line-height: 1.1428;
}

.heading-3 {
    font-size: var(--fs-4);
    line-height: 1.25;
}

.body-1 {
    font-size: var(--fs-1);
    line-height: 1.75;
}

.body-2 {
    font-size: var(--fs-2);
    line-height: 1.777;
}

sources.scss/layouts/_container.scss:
@use '../abstracts' as *;

.container {
    --container-max-width: 1110px;
    max-inline-size: var(--container-max-width);
    margin-inline: auto;

    @media #{$break1} {
        --container-margin: 1rem;
        width: calc(100% - (var(--container-margin) * 2));
    }

    @media #{$break2} {
        --container-margin: 2.5rem;
        width: calc(100% - (var(--container-margin) * 2));
    }

    @media #{$break3open} {
        --container-margin: 4rem;
        width: calc(100% - (var(--container-margin) * 2));
    }
}

sources.scss/layouts/_hero.scss:
@use '../abstracts' as *;

.hero {
    background: hsl(var(--clr-primary-400));
    text-align: center;

    @media #{$break1}, #{$break2} {
        padding-block-start: pxToRem(32);
    }

    @media #{$break3open} {
        padding-block-start: pxToRem(56);
    }

    &-content {
        margin-inline: auto;

        @media #{$break1} {
            max-inline-size: 100%;
        }

        @media #{$break2} {
            max-inline-size: pxToRem(570);
        }

        @media #{$break3open} {
            max-inline-size: pxToRem(640);
        }
    }

    &-title {
        color: hsl(var(--clr-neutral-100));
        margin-block-end: pxToRem(40);
    }

    &-img {
        position: relative;
        top: 5em;
    }
}


.hero-title-underline {
    position: relative;
}

.hero-title-underline::before {
    @include text-underline(hsl(var(--clr-secondary-400)), 3px, 100%, 5px);
    left: 3px;
}

sources.scss/abstracts/_mixins.scss:
@use 'variables' as *;

@mixin text-underline($color, $size, $width, $distance) {
    content: '';
    block-size: $size;
    inline-size: $width;
    bottom: $distance;
    background: $color;
    position: absolute;
}

