sources.scss/modules/_buttons.scss:
@use '../abstracts' as *;

%btn {
    display: inline-block;
    font-weight: 700;
    padding: 0.8em 1.5em;
    letter-spacing: -0.16px;

    @media #{$break1}, #{$break2} {
        font-size: var(--fs-body-1);
        line-height: 2;
    }

    @media #{$break2open} {
        font-size: var(--fs-body-2);
        line-height: 1.777;
    }
}

.btn-secondary {
    @extend %btn;
    background-color: hsl(var(--clr-secondary-400));
    color: hsl(var(--clr-primary-400));

    &:hover {
        background-color: transparent;
        border: 2px solid hsl(var(--clr-secondary-400));
        color: hsl(var(--clr-secondary-400));
    }
}

sources.scss/modules/_links.scss:
@use '../abstracts' as *;

%link {
    display: inline-block;
    font-size: var(--fs-2);
    font-weight: var(--fw-bold);
    line-height: 1.777;
}

.link-neutral-100 {
    @extend %link;

    color: hsl(var(--clr-neutral-100));
    border-bottom: 3px solid hsl(var(--clr-secondary-400));
}
