sources.scss/layouts/_container.scss:
@use '../abstracts' as *;

.container {
    max-inline-size: $containter-max-inline-size;
    margin-inline: auto;

    @media #{$break1} {
        @include containerMargin(1rem);
    }
    @media #{$break2} {
        @include containerMargin(2.5rem);
    }
    @media #{$break3open} {
        @include containerMargin(10.25rem);
    }
}

sources.scss/layouts/_footer.scss:
@use '../abstracts' as *;

.site-footer {
    background: clr(neutral, dark);
    color: clr(neutral, light);

    @media #{$break1} {
        padding-block: 3rem;
    }
    @media #{$break2} {
        padding-block: 3.5rem;
    }
    @media #{$break3open} {
        padding-block: 5rem;
    }
}

sources.scss/modules/_buttons.scss:
@use '../abstracts' as *;

%btn {
    display: inline-flex;
    cursor: pointer;
    font-size: 20px;
    line-height: 24px;
    letter-spacing: 3.6366px;
    text-transform: uppercase;
    font-weight: 800;
    align-items: center
}

.btn-primary-chevron {
    @extend %btn;
    background-color: clr(neutral, dark);
    color: clr(neutral, light);
}

.btn-text {
    display: inline-block;
    padding-inline: 32px;
}

.btn-icon {
    background-color: clr(primary);
    display: flex;
    justify-content: center;
    align-items: center;
    padding: 24px;
}
