/***
    The new CSS reset - version 1.8.4 (last updated 14.2.2023)
    GitHub page: https://github.com/elad2412/the-new-css-reset
***/

/*
    Remove all the styles of the "User-Agent-Stylesheet", except for the 'display' property
    - The "symbol *" part is to solve Firefox SVG sprite bug
 */
 *:where(:not(html, iframe, canvas, img, svg, video, audio):not(svg *, symbol *)) {
    all: unset;
    display: revert;
}

/* Preferred box-sizing value */
*,
*::before,
*::after {
    box-sizing: border-box;
}

/* Reapply the pointer cursor for anchor tags */
a, button {
    cursor: revert;
}

/* Remove list styles (bullets/numbers) */
ol, ul, menu {
    list-style: none;
}

/* For images to not be able to exceed their container */
img {
    max-inline-size: 100%;
    max-block-size: 100%;
}

/* removes spacing between cells in tables */
table {
    border-collapse: collapse;
}

/* Safari - solving issue when using user-select:none on the <body> text input doesn't working */
input, textarea {
    user-select: auto;
}

/* revert the 'white-space' property for textarea elements on Safari */
textarea {
    white-space: revert;
}

/* minimum style to allow to style meter element */
meter {
    -webkit-appearance: revert;
    appearance: revert;
}

/* preformatted text - use only for this feature */
:where(pre) {
    all: revert;
}

/* reset default text opacity of input placeholder */
::placeholder {
    color: unset;
}

/* remove default dot (•) sign */
::marker {
    content: initial;
}

/* fix the feature of 'hidden' attribute.
   display:revert; revert to element instead of attribute */
:where([hidden]) {
    display: none;
}

/* revert for bug in Chromium browsers
   - fix for the content editable attribute will work properly.
   - webkit-user-select: auto; added for Safari in case of using user-select:none on wrapper element*/
:where([contenteditable]:not([contenteditable="false"])) {
    -moz-user-modify: read-write;
    -webkit-user-modify: read-write;
    overflow-wrap: break-word;
    -webkit-line-break: after-white-space;
    line-break: after-white-space;
    user-select: auto;
}

/* apply back the draggable feature - exist only in Chromium and Safari */
:where([draggable="true"]) {
    -webkit-user-drag: element;
}

/* Revert Modal native behavior */
:where(dialog:modal) {
    all: revert;
}

sources.scss/abstracts/_variables.scss:
/* container */
$containter-max-inline-size: 1110px;

/* colors */
$clr-primary:     hsl(24, 56%, 63%);
$clr-neutral-100: hsl(0, 0%, 100%);
$clr-neutral-400: hsl(0, 0%, 27%);
$clr-neutral-800: hsl(0, 0%, 8%);

$colors: (
    primary: (
        base:  $clr-primary,
    ),
    neutral: (
        light: $clr-neutral-100,
        base:  $clr-neutral-400,
        dark:  $clr-neutral-800,
    )
);

/* typography */
$ff-primary: 'Outfit', sans-serif;
$ff-secondary: 'Big Shoulders Display', sans-serif;
$ff-body: $ff-primary;
$ff-heading: $ff-secondary;

$fs-1: 1.125rem;  // 18px
$fs-2: 1.25rem;   // 20px
$fs-3: 1.375rem;  // 22px
$fs-4: 2.25rem;   // 36px
$fs-5: 3.125rem;  // 50px
$fs-6: 3.75rem;   // 60px
$fs-7: 4.375rem;  // 70px
$fs-8: 6rem;      // 96px

$fs-body-1: $fs-1;
$fs-body-2: $fs-3;
$fs-btn:    $fs-2;

$fw-light: 300;
$fw-bold:  800;
$fw-black: 900;

