sources.scss/layouts/_hero.scss:
@use '../abstracts' as *;

.hero {
    position: relative;

    @media #{$break3open} {
        &::before {
            content: '';
            position: absolute;
            inset: 0 50% 0 0;
            z-index: -5;
            background-color: clr(neutral, dark);
        }
    }
}

.hero-container {
    min-block-size: pxToRem(700);
    display: flex;

    @media #{$break1} {
        flex-direction: column;
    }
    @media #{$break2} {
        align-items: center;
        justify-content: end;

    }
    @media #{$break3open} {
        align-items: center;
    }
}


.hero-img {
    @media #{$break1} {
        inline-size: 100vw;
        max-inline-size: 100vw;
        display: block;
        margin: 0 calc(50% - 50vw) 2rem;
    }
    @media #{$break2}, #{$break3open} {
        position: absolute;
        z-index: -1;
        top: 0;
    }
    @media #{$break2} {
        left: 0;
    }
    @media #{$break3open} {
        left: 50%;
        transform: translateX(-50%);
    }
}


.hero-text {

    @media #{$break1} {
        .hero-description {
            padding-block: 2rem;
        }
    }

    @media #{$break2} {
        max-inline-size: pxToRem(340);

        .hero-description {
            padding-block: 3rem;
        }
    }

    @media #{$break3open} {
        display: flex;
        justify-content: space-between;
        inline-size: 100%;

        .hero-title {
            mix-blend-mode: difference;
            color: clr(neutral, light);
        }
        .hero-text-content {
            max-inline-size: pxToRem(350);
        }
        .hero-description {
            padding-block-end: 4rem;
        }
    }
}

sources.scss/layouts/_grids.scss:
@use '../abstracts' as *;

.grid-home {
    display: grid;

    @media #{$break1} {
        gap: 1rem;
    }

    @media #{$break2open} {
        grid-template-columns: repeat(12, 1fr);
        gap: pxToRem(11);
        grid-template-areas: 
        "c-1 c-1 c-1 c-1  c-1 i-1 i-1 i-1  i-1 i-1 i-1 i-1"
        "i-2 i-2 i-2 i-2  i-2 i-2 i-2 i-3  i-3 i-3 i-3 i-3"
        "i-2 i-2 i-2 i-2  i-2 i-2 i-2 c-2  c-2 c-2 c-2 c-2";
    }

    @media #{$break3open} {
        gap: pxToRem(30);
    }

}

@media #{$break2open} {
    .grid-home-item-1 {
        grid-area: c-1;
    }
    .grid-home-item-2 {
        grid-area: i-1;
    }
    .grid-home-item-3 {
        grid-area: i-2;
    }
    .grid-home-item-4 {
        grid-area: i-3;
    }
    .grid-home-item-5 {
        grid-area: c-2;
    }
}

.grid-home img {
    display: block;
    object-fit: cover;
    min-width: 100%;
    min-height: 100%;
}

/* grid-1-2-1 */
.grid-1-2-1 {
    display: grid;
    grid-template-columns: 1fr 2fr 1fr;
}

