sources.scss/layouts/_footer.scss:
@use '../abstracts' as *;

#footer {
    color: clr(neutral, lightest);

    @media #{$break1} {
        padding-block: 2.5rem;
    }

    @media #{$break2open} {
        padding-block: 3rem;
    }
}


.l-footer {
    display: grid;

    @media #{$break1} {
        justify-content: center;
        gap: 2rem;

        &>* {
            margin-inline: auto;
        }
    }

    @media #{$break2open} {
        grid-template-columns: 1fr 2fr 1fr;
        row-gap: 1.5rem;
    }
}


.footer-links {
    display: grid;
    font-size: $fs-3;
    line-height: 1.7333;
    grid-auto-rows: min-content;
    gap: 0.5rem;

    @media #{$break1} {
        &-item {
            text-align: center;
        }
    }

    @media #{$break2open} {
        grid-template-columns: repeat(2, 10em);

        grid-column: 2 / 3;
        grid-row: 1 / 3;
        justify-self: start;
    }

}

.footer-socials {
    @media #{$break2open} {
        grid-column: 1 / 2;
        grid-row: 2 / 3;
    }


}

.footer-copywrite {
    opacity: 0.5;
    @media #{$break2open} {
        grid-column: 3 / 4;
        grid-row: 2 / 3;
        text-align: right;
        font-size: $fs-4;
        line-height: 1.7333;
    }


}

.footer-btn {
    @media #{$break2open} {
        max-inline-size: max-content;
        justify-self: end;
    }
}

sources.scss/modules/_buttons.scss:
@use '../abstracts' as *;

%btn {
    --p-btn-font-size: #{$fs-3};
    --p-btn-font-weight: #{$fw-bold};
    --p-btn-border-radius: 100vw;
    --p-btn-padding: .5714em 2.3571em;

    display: inline-flex;
    font-size: var(--p-btn-font-size);
    font-weight: var(--p-btn-font-weight);
    line-height: 2;
    padding: var(--p-btn-padding);
    border-radius: var(--p-btn-border-radius);

    &:hover,
    &:focus-visible {
        transform: scale(0.98);
    }

    &:focus-visible {
        @include outline(clr(primary));
    }

    @supports not selector(:focus-visible) {
        &:focus {
            @include outline(clr(primary));
        }
    }
}

.btn-gradient-400 {
    @extend %btn;
    background: $gradient-400;
    color: clr(neutral, lightest);
}

