@use '../abstracts' as *;

.overlay::before {
    --p-overlay-color: #{$clr-primary-400-hsla};

    @media #{$break1}, #{$break2} {
        content: '';
        position: fixed;
        inset: 0;
        background: linear-gradient(hsl(var(--p-overlay-color)), hsl(var(--p-overlay-color), 0.01));
        transition: 0.3s ease-in-out;
    }
}

.overlay-hidden::before {
    opacity: 0;
    z-index: -10;
}

.overlay-visible::before {
    @media #{$break1}, #{$break2} {
        z-index: 100;
        opacity: 1;
    }
}

